mapper.select:
select no, subject, name, regdate, TO_CHAR(regdate, 'yyyy-MM-dd') as day, hit, group_tab, num
from (SELECT no, subject, name, regdate, hit, group_tab, rownum as num
	from(SELECT no, subject, name, regdate, hit, group_tab
		from mvcBoard order by group_id desc, group_step asc				
	)
) where num between #{start} and #{end}

mapper.update:
UPDATE mvcBoard SET
hit=hit+1
where no=#{no}

mapper.select:
Select no, name, subject, content, TO_CHAR(regdate, 'YYYY-MM-DD') as day, hit
FROM mvcBoard
WHERE no=#{no}

mapper.update:
UPDATE mvcBoard SET
name=#{name}, subject=#{subject}, content=#{content}
WHERE no=#{no}

mapper.select:
SELECT group_id, group_step, group_tab
FROM mvcBoard
WHERE no=#{no}

mapper.update:
update mvcBoard set
group_step=group_step+1
where group_id=#{group_id} and group_step>#{group_step}

UPDATE mvcBoard SET
depth=depth+1
where no=#{no}
